import React, {Component} from 'react';
import { getTrades, getDataFromInquiry, getAllTradesWithFilters } from './_services'
import { formatDate } from './_helpers'

import ChartStock from './components/Chart';
import { TypeChooser } from "react-stockcharts/lib/helper";

import { styles } from "./App.css";


class App extends Component {
  constructor(props) {
    super(props);
    this.period = 300;
    this.BTCOptions = [1,2,5,10];

    const date = new Date(),
      end = formatDate(date);


    this.state = {
      selectedPair: 'ADABTC',
      data: [],
      dataFromBack: [],
      dataTrades: [],
      timeStep: 'candles_daily',
      date: {
        start: formatDate(this.minusDays(date, this.period)),
        end: end
      },
      dateTrades : {
        start: formatDate(this.minusHours(date, 2)),
        end: end
      },
      pairs: [],
      btcPrice: 1,
      selectedBtc: this.BTCOptions[0],
      btcLoading: false,
      reqSended: false,
      lastRequest: false,
      noRecords: false,
      noTradesData: false,
      loadingCounter: 0
    };

  }

  componentDidMount() {
    this.loadPairs();
    this.loadBTC();
    this.loadData(this.state.date.start, this.state.date.end, this.state.selectedPair, this.state.timeStep);
  }

  minusHours = (date, h) => {
    let result = new Date(date);
    result.setHours(result.getHours() - h);
    return result;
  };

  minusDays = (date, days) => {
    let result = new Date(date);
    result.setDate(result.getDate() - days);
    return result;
  };

  minusMinutes = (date, minutes) => {
    let result = new Date(date);
    result.setMinutes(result.getMinutes() - minutes);
    return result;
  };


  loadBTC = () => {
    getDataFromInquiry('btc_price').then(data => {
      this.setState({
        ...this.state,
        btcPrice: data[0]
      })
    }).catch(err => console.log(err));
  };

  loadPairs = () => {
    getDataFromInquiry('pairs_list').then(data => {
      data.unshift('ALL');

      this.setState({
        ...this.state,
        pairs: data
      })
    }).catch(err => console.log(err));
  };


  loadData = (start, end, symbol = this.state.selectedPair, inquiry = this.state.timeStep) => {
    if (!this.state.noRecords) {

      getTrades(start, end, symbol, inquiry).then(data => {
        let dataFromBack = [...data];

        if (this.state.dataFromBack.length) {
          dataFromBack = dataFromBack.concat(this.state.dataFromBack);
        }

        this.setState({
            data: dataFromBack,
            dataFromBack: dataFromBack,
            reqSended: false,
            date: {
              start: start,
              end: end
            },
            loadingCounter: ++this.state.loadingCounter,
            lastRequest: true
          },
          () => {
            if (this.state.loadingCounter === 1 && this.state.timeStep === 'candles_hourly') {
              this.updateDate();
            }
          }
        );
      }).catch(err => {
        console.log(err);
        this.setState({
          ...this.state,
          reqSended: false,
          noRecords: true,
          lastRequest: true
        });
      })
    }
  };


  getStartDate = (timeStep, end, minutesCoefficient = 5) => {
    let start = {};

    switch (timeStep) {
      case 'candles_daily':
        start =  formatDate(this.minusDays(end, this.period));
        break;
      case 'candles_hourly':
        start = formatDate(this.minusHours(end, this.period));
        break;
      case 'minutes':
        start = formatDate(this.minusMinutes(end, this.period * minutesCoefficient));
        break;
    }

    return start;
  };

  updateDate = (period = 200) => {
    if (this.state.reqSended === false || this.state.noRecords) {
      let { date, timeStep } = this.state;

      let end =  date.start,
        start = this.getStartDate(this.state.timeStep, end, 1);

      this.loadData(start, end, this.state.selectedPair, this.state.timeStep)
    }
  };

  loadAllTradesData = () => {
    let date = new Date();
    let start = formatDate(this.minusHours(date, 10));
    let end = formatDate(date);

    this.setState({
      ...this.state,
      selectedPair: 'ALL',
      btcLoading: true,
      dateTrades : {
        start: start,
        end: end
      },
    }, () => {
      getAllTradesWithFilters(start, end, this.state.btcPrice, this.state.selectedBtc).then(data => {
        this.setState({
          ...this.state,
          dataTrades: data,
          btcLoading: false,
          dateTrader: {
            start: start,
            end: end,
          },
        });
      }).catch(err => {
        this.setState({...this.state, noTradesData: true});
        console.log(err);
      })

    });
  };

  handlePairChange = (pair) => {
    if (pair === this.state.selectedPair) return;

    const end =  formatDate(new Date()),
      start = this.getStartDate(this.state.timeStep, end);


    if (pair === 'ALL') {
      this.loadAllTradesData();
      return;
    }

    this.setState({
      ...this.state,
      selectedPair: pair,
      data: [],
      dataFromBack: [],
      dataTrades: [],
      loadingCounter: 0,
      date: {
        start: start,
        end: end
      },
      noRecords: false,
      noTradesData: false,
      lastRequest: false
    }, () => {
      this.loadData(start, end, this.state.selectedPair)
    });
  };

  changeTimeStep = (timeStep) => {
    if (timeStep === this.state.timeStep) return ;

    this.setState({
        ...this.state,
        data: [],
        dataFromBack: [],
        loadingCounter: 0,
        timeStep: timeStep,
        noRecords: false,
        lastRequest: false
      },
      () => {
        const end =  formatDate(new Date()),
          start = this.getStartDate(timeStep, end);
        this.loadData(start, end, this.state.selectedPair, timeStep);
      }
    );
  };

  handleBTCChange = (event) => {
    this.setState({
      ...this.state,
      selectedBtc: +event.target.value
    }, () => this.loadAllTradesData());
  };


  render() {
    let {data, pairs, selectedPair, dataTrades} = this.state;

    if (pairs.length === 0) {
      return (
        <div style={{textAlign: 'center'}}>
          <img alt="" src="data:image/svg+xml;base64," />
        </div>
      )
    }

    return (
      <div>
        <main className="d-flex">
          <div className="col-2">
            <ul className="list-unstyled">
              {pairs.map((pair, index) => (
                  (selectedPair !== pair) ? (
                    <li key={'li-' + index}>
                      <span data-pair={pair} onClick={this.handlePairChange.bind(null, pair)}>{pair}</span>
                    </li>
                  ) : (
                    <li key={'li-' + index} className="active">
                      <span data-pair={pair} onClick={this.handlePairChange.bind(null, pair)}>{pair}</span>
                    </li>
                  )
                )
              )}
            </ul>
          </div>

          <div className="col-10 right">
            {(this.state.selectedPair !== "ALL") && (
              <div>
                <p>Time step:
                  <button className={"btn " + (this.state.timeStep === 'candles_daily' ? 'active' : '')}
                          onClick={() => this.changeTimeStep('candles_daily')}>Days</button>

                  <button className={"btn " + (this.state.timeStep === 'candles_hourly' ? 'active' : '')}
                          onClick={() => this.changeTimeStep('candles_hourly')}>Hours</button>

                  <button className={"btn " + (this.state.timeStep === 'minutes' ? 'active' : '')}
                          onClick={() => this.changeTimeStep('minutes')}>Minutes</button>
                </p>
                <div className="chart-wrap">
                  {(data.length && this.state.lastRequest) ?
                    <TypeChooser>
                      {type => <ChartStock type={type} data={data} updateDate={this.updateDate} timeStep={this.state.timeStep} />}
                    </TypeChooser>
                    :
                    <div style={{textAlign: 'center'}}>
                      <img alt="" src="data:image/svg+xml;base64," />
                    </div>
                  }
                  {this.state.noRecords && <p>No more records!</p>}
                </div>
              </div>
            )}

            {(this.state.noTradesData) && (
              <div className="mt-5 btc-wrap">
                <div className="d-flex">
                  No trades data.
                </div>
              </div>
            )}

            {(selectedPair === 'ALL') && (
              (dataTrades.length > 0) ? (
                <div className="mt-5 btc-wrap">
                  <div className="d-flex">
                    <label htmlFor="BTCValue">BTC Value:</label>
                    <select id="BTCValue" value={this.state.value} onChange={this.handleBTCChange}>
                      {this.BTCOptions.map((option, index) => (
                          <option value={option} key={'option-' + index}>{option}</option>
                        )
                      )}
                    </select>
                  </div>

                  {(this.state.btcLoading) && (
                    <div className="ml-5">
                      <img alt="" src="data:image/svg+xml;base64," />
                    </div>
                  )}

                  <table className="table mt-4">
                    <thead>
                    <tr>
                      <th>Date</th>
                      <th>Pair</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      <th>Amount</th>
                      <th>Operation</th>
                    </tr>
                    </thead>
                    <tbody>
                    {dataTrades.map((trade, index) => (
                        <tr key={'trade-' + index}>
                          <td>{formatDate(trade.data)}</td>
                          <td>{trade.pair}</td>
                          <td>{trade.price}</td>
                          <td>{trade.quantity}</td>
                          <td>{trade.amount}</td>
                          <td>{trade.operation}</td>
                        </tr>
                      )
                    )}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="ml-5">
                  <img alt="" src="data:image/svg+xml;base64," />
                </div>
              )
            )}
          </div>

        </main>
      </div>
    );
  }
}


export default App;
